mapper.delete:
        UPDATE YSL.MEMO
           SET delyn = 'Y'
		 where titleno = #{titleno}
		   and userid = #{userid}

mapper.insert:
 insert into ysl.memo
 select NVL(max(titleno),0)+1
 	 , 'N'
 	 , #{newText}
 	 , #{userid}
	  from ysl.memo

mapper.select:
        select 'Y' from ysl.userinfo
	     where userid = #{userid}
	       and password = #{password}
	       and delyn = 'N'

SELECT titleno,textno,text,userid FROM YSL.DTMEMO
 WHERE delyn = 'N'
   and userid = #{userid}
   and titleno = #{titleno}
 order by textno

mapper.delete:
        UPDATE YSL.DTMEMO
           SET delyn = 'Y'
		 where titleno = #{titleno}
		   and userid = #{userid}
		   and textno = #{textno}

mapper.insert:
 insert into ysl.dtmemo
 select #{titleno}
 	 , NVL(max(textno),0)+1
 	 , #{newText}
 	 , 'N'
 	 , #{userid}
	  from ysl.dtmemo
